<script>
export default {
  methods: {
    setDurations(){
      let paths = document.querySelectorAll('.bdConfetty path');
      console.log(paths);
      for(let i = 0; i < paths.length; i++){
        const min = 2,
              max = 5;
        paths[i].style.animationDelay = (Math.random() * (-max + min) - min) + 's';
        paths[i].style.animationDuration = (Math.random() * (max - min) + min) * 2.5 + 's';
      }
      // document.querySelector('.bdConfetty path').each(function(index){
      //
      // });
    }
  },
  mounted(){
    this.setDurations();
  }
}
</script>

<style lang="scss" scoped>
  @keyframes transformFall{
    0% {
      -webkit-transform: translateY(-100%);
      -ms-transform: translateY(-100%);
      transform: translateY(-100%);
      opacity: 0;
    }
    1%{
      opacity: 100%;
    }
    100%{
      opacity: 100%;
      -webkit-transform: translateY(100%);
      -ms-transform: translateY(100%);
      transform: translateY(100%);
    }
  }
  path, rect{
    opacity: 1;
    position: absolute;
    transform: translateY(-100%);
    animation: transformFall linear infinite;
  }
</style>
